import MyReact from './MyReact'

const MyReactDOM = {
    render,
    setAttribute,
    renderComponent
}

function createDomFromVnode(vnode) {
    //vnode可能为空则直接返回
    if (!vnode) {
        return
    }

    if(Array.isArray(vnode)) {
        let fragment = document.createDocumentFragment()
        vnode.forEach( node => {
            let dom = createDomFromVnode(node)
            fragment.append(dom)
        })
        return fragment
    }

    if (typeof vnode === 'string' || typeof vnode === 'number') {
        return document.createTextNode(vnode)
    }

    if (typeof vnode === 'object') {
        if (typeof vnode.tag === 'function') {
            let component = createComponent(vnode.tag, vnode.attrs) //创建一个组件
            renderComponent(component)
            return component.$root
        } else {
            let dom = document.createElement(vnode.tag)
            setAttribute(dom, vnode.attrs)
            if (Array.isArray(vnode.children) && vnode.children.length > 0) {
                vnode.children.map(vnodeChild => {
                    _render(vnodeChild, dom)
                })
            }
            return dom
        }
    }
}

function createComponent(constructor, attrs) {
    //创建组件,设置组件属性
    let component

    //如果是用class创建的
    if (constructor.prototype instanceof MyReact.Component) {
        component = new constructor(attrs)
    } else {
        //如果是用function创建的
        component = new MyReact.Component(attrs)
        component.constructor = constructor //constructor重新指向原来的构造函数
        component.render = function () {
            console.log(attrs)
            return this.constructor(attrs) //调用父类的constructor方法使得子类有props和state属性
        }
        console.log(component)
    }

    return component
}

function _render(vnode, container) {
    //vnode可能为空字符串,null,undefined等
    if (!vnode) {
        return
    }
    let dom = createDomFromVnode(vnode)
    return container.append(dom)
}

function renderComponent(component) {
    let vnode = component.render()
    console.log('组件返回的vnode', vnode)

    let dom = createDomFromVnode(vnode)  //通过虚拟DOM来创建dom

    if (component.$root && component.$root.parentNode) {
        component.$root.parentNode.replaceChild(dom, component.$root)
    }
    component.$root = dom
}

function render(vnode, container) {
    console.log(vnode, container)  //当vnode为class组件时，vnode = {tag: f,attrs:null,children: []}
    container.innerHTML = ''
    _render(vnode, container)
}


//给DOM元素设置属性
function setAttribute(dom, attrs) {
    if (!attrs) {
        return
    }
    for (let key in attrs) {
        if (/^on/.test(key)) {
            dom[key.toLowerCase()] = attrs[key]
        } else if (key === 'style') {
            //这样直接赋值的话，style属性和onclick事件就没法绑定,需要做一个判断
            Object.assign(dom.style, attrs[key])
        } else {
            dom[key] = attrs[key]
        }
    }
}

export default MyReactDOM
